(function(){var isShow=!1,lock=!1;var $btn=$('.back-to-top');$(document).scroll(function(){if(lock)return
  if($(this).scrollTop()>=1){if(!isShow)$btn.addClass('load')
  isShow=!0}else{if(isShow){$btn.removeClass('load')
  isShow=!1}}})
  $btn.click(function(){lock=!0
  $btn.addClass('ani-leave')
  $("html, body").animate({scrollTop:0},800);setTimeout(function(){$btn.removeClass('ani-leave').addClass('leaved')},390)
  setTimeout(function(){$btn.addClass('ending')},120)
  setTimeout(function(){$btn.removeClass('load')},1500);setTimeout(function(){lock=!1
  isShow=!1
  $btn.removeClass('leaved ending')},2000)})})()