    <script type="text/javascript">
        window.onload = function () {   
            var targetElement = document.getElementById('typecho-option-item-CheckClick-0');
            //   style.display="none";
            if (targetElement) {
                // 总览的自定元素
                let htmlToInsert = 
                `<div style="padding: 20px 10px 12px 25px;margin-left:200px;" id="html-basic-config">
                    <span class="env-title">环境信息</span>
                    <div class="environment">
                        <div class="env-typecho">
                            <p>Typecho版本</p>
                            <p class="mysqlversion">{{ typechoVersion }}</p>
                        </div>
                        <div class="env-php">
                            <p>PHP版本</p>
                            <p class="phpversion">{{ phpversion }}</p>
                        </div>
                        <div class="env-mysql">
                            <p>MySQL版本</p>
                            <p class="mysqlversion">{{ mysqlversion }}</p>
                        </div>
                    </div>
                    <span class="env-title">网站信息</span>
                    <div class="environment">
                        <div class="article-message">
                            <p class="article-title" style="margin-bottom: 15px;">文章信息</p>
                            <p style="margin-bottom:5px;">文章总量</p>
                            <p class="articleCount" style="margin-bottom:5px;">{{ articleCount }}</p>
                            <p style="display:flex;justify-content:space-between;">
                                <span>已发布</span>
                                <span style="margin-right:10px;">{{ publishedCount }}</span>
                            </p>
                            <p style="display:flex;justify-content:space-between;">
                                <span>草稿箱</span>
                                <span style="margin-right:10px;">{{ draftCount }}</span>
                            </p>
                        </div>
                        <div class="comment-message">
                            <p class="comment-title" style="margin-bottom: 15px;">留言信息</p>
                            <p style="margin-bottom:5px;">评论信息</p>
                            <p class="articleCount" style="margin-bottom:5px;">{{ commentCount }}</p>
                            <p style="display:flex;justify-content:space-between;">
                                <span>已发布</span>
                                <span style="margin-right:10px;">{{ publishedCommentCount }}</span>
                            </p>
                            <p style="display:flex;justify-content:space-between;">
                                <span>待审核</span>
                                <span style="margin-right:10px;">{{ pendingCommentCount }}</span>
                            </p>
                        </div>
                        <div class="links-message">
                            <p class="links-title" style="margin-bottom: 15px;">友链信息</p>
                            <p style="margin-bottom:5px;">友情链接总量</p>
                            <p class="articleCount" style="margin-bottom:5px;">{{ linkCount }}</p>
                            <p style="display:flex;justify-content:space-between;">
                                <span>本站链接</span>
                                <span style="margin-right:10px;">{{ linkCount }}</span>
                            </p>
                            <p style="display:flex;justify-content:space-between;">
                                <span> &nbsp;</span>
                                <span style="margin-right:10px;"> </span>
                            </p>
                        </div>
                    </div>
                    <span class="env-title">插件信息</span>
                    <div class="environment">
                        <div class="plugins-message">
                            <p class="plugins-input">介入插件项</p>
                            <p>{{ inputCount }}</p>
                        </div>
                    </div>
                </div>`;
                // 关于插件的自定元素
                let AboutPluginsElement = 
                `<div style="padding: 20px 10px 12px 20px; margin-left: 200px; "id="about-plugins-config">
                    <div class="about_logo">
                        <img src="/typecho/usr/plugins/comic/static/image/logo.webp"/>
                    </div>
                    <div class="about_plugins">
                        <span>Comic</span>
                        <span class="about_plugins_version">v{{ version }}</span>
                    </div>
                    <div>
                        <p><span>本插件由<a href="https://starsei.com" target="_blank">starsei</a>开发<span/></p>
                        <div class="about_me_a">
                            <a href="https://docs.starsei.com/" target="_blank"><svg t="1713664256526" class="icon" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg" p-id="10484" width="22" height="22"><path d="M343.797232 455.997905c40.893277-45.039913 78.217091-77.08526 111.961208-96.126832 33.744117-19.039525 62.4697-28.560823 86.194146-28.560823 17.051146 0.858595 42.007711-1.751984 40.490075 31.939942 0 11.881155-13.272918 62.247632-39.816707 151.098408-43.285882 145.370688-64.927799 233.004695-64.927799 262.889739 0 5.737953 1.430651 10.545676 4.299116 14.43238 2.859255 3.89796 5.931367 5.836195 9.214291 5.836195 13.100994 0 124.688678-52.821506 124.688678-52.821506-56.106477 52.821506-97.046828 86.604511-122.847662 101.346967-25.797763 14.741433-46.892186 22.111638-63.264847 22.111638-14.337208 0-25.701568-4.51095-34.087965-13.512383-8.398677-9.00348-12.592387-21.497624-12.592387-37.46913 0-51.182091 30.711915-176.282158 92.1337-375.290991 2.859255-9.414869 13.956519-41.077481-6.757215-37.468107C460.287811 404.403402 343.797232 455.997905 343.797232 455.997905zM578.606102 111.750397c16.412572 0 29.920862 5.437087 40.525893 16.289769 10.590704 10.862916 15.889638 24.768268 15.889638 41.716055 0 23.316127-8.080413 43.838494-24.233053 61.580404-16.166967 17.754191-34.306963 26.618494-54.431244 26.618494-15.892708 0-29.137995-5.561936-39.728699-16.686831-10.605031-11.123872-15.892708-25.688264-15.892708-43.702387 0-23.838038 7.672095-44.100473 23.041868-60.787304C539.137335 120.093813 557.414461 111.750397 578.606102 111.750397z" p-id="10485"></path></svg>ReadMe</a>
                            <a href="https://pan.starsei.com/%E5%8D%9A%E5%AE%A2/typecho/plugins/Comic/" target="_blank"><svg t="1713664470714" class="icon" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg" p-id="15523" width="22" height="22"><path d="M505.7 661c3.2 4.1 9.4 4.1 12.6 0l112-141.7c4.1-5.2 0.4-12.9-6.3-12.9h-74.1V168c0-4.4-3.6-8-8-8h-60c-4.4 0-8 3.6-8 8v338.3H400c-6.7 0-10.4 7.7-6.3 12.9l112 141.8z" p-id="15524"></path><path d="M878 626h-60c-4.4 0-8 3.6-8 8v154H214V634c0-4.4-3.6-8-8-8h-60c-4.4 0-8 3.6-8 8v198c0 17.7 14.3 32 32 32h684c17.7 0 32-14.3 32-32V634c0-4.4-3.6-8-8-8z" p-id="15525"></path></svg>DownLoad</a>
                        </div>
                    </div>
                </div>`;
                // 关于插件的自定元素
                let UpdatePluginsElement = 
                `<div style="padding: 20px 10px 12px 25px; margin-left: 200px; "id="update-plugins-config">
                    <div class="update_plugins">
                        <span>插件版本</span>
                        <span class="update_plugins_version">v{{ version }}</span>
                    </div>
                    <div id="update-message">{{ updateMessage }}</div>
                    <div>
                        <div class="update_a">
                            <a href="javascript:void(0)" id="updateLink">点击刷新最新状态</a>
                        </div>
                    </div>
                    <div class="download_plugins">
                        <span>下载插件</span>
                    </div>
                    <div>
                        <div class="download_a">
                            <a href="https://pan.starsei.com/%E5%8D%9A%E5%AE%A2/typecho/plugins/Comic/" target="_blank">Alist传送门</a>
                        </div>
                    </div>
                </div>`;
                // 关于插件的插入元素
                var AboutElement = document.createElement('div');
                AboutElement.innerHTML = AboutPluginsElement;
                targetElement.parentNode.insertBefore(AboutElement.firstChild, targetElement);

                var UpdateElement = document.createElement('div');
                UpdateElement.innerHTML = UpdatePluginsElement;
                targetElement.parentNode.insertBefore(UpdateElement.firstChild, targetElement);

                // 创建一个临时的容器元素
                var tempContainer = document.createElement('div');
                tempContainer.innerHTML = htmlToInsert;
                // 插入新内容到目标元素前面
                targetElement.parentNode.insertBefore(tempContainer.firstChild, targetElement);
                let isLoading = false;
                document.getElementById('updateLink').addEventListener('click', function (event) {
                    if (!isLoading) {
                        isLoading = true;
                        let dots = '';
                        let intervalId = setInterval(() => {
                        dots += '.';
                        document.getElementById('update-message').innerHTML = '加载中' + dots;
                        if (dots.length === 3) {
                            dots = '';
                        }
                        }, 500);

                        // 模拟异步操作，这里可以替换为实际的请求或操作
                        setTimeout(() => {
                        clearInterval(intervalId);
                        document.getElementById('update-message').innerHTML = '{{ updateMessage }}';
                        isLoading = false;
                        }, 2000);
                    }
                });     
            }
            showContent('html-basic-config','btn-basic-config'); 
        };
    
        // @param currentId  显示或隐藏ul标签id
        // @param buttonId  按钮id
        // @showElements
        function showContent(currentId,buttonId,...showElements)
        {   
            var CheckClick = document.getElementById(currentId);
            // 获取总览
            var BasicConfig = document.getElementById('html-basic-config');
            // 获取关于插件
            var AboutPlugins = document.getElementById('about-plugins-config');
            // 获取更新插件
            var UpdatePlugins = document.getElementById('update-plugins-config');
            // 获取提交按钮
            var button = document.getElementById("typecho-option-item--14");
            // 获取表单元素
            var form = document.getElementsByTagName('form')[0];
            // 获取当前插件设置所有ul列表项
            var elements = document.getElementsByTagName('ul'); 
            for (var i = 0; i < elements.length; i++) {
                if (elements[i].id && elements[i].id !== currentId) {
                    elements[i].style.display = 'none';
                    // 选择其他模块时当前总览隐藏      
                    BasicConfig.style.display = 'none';
                    AboutPlugins.style.display = 'none';
                    UpdatePlugins.style.display= 'none';
                }
                CheckClick.style.display = "block";
            } 
            // 选择判断是否隐藏保存设置按钮
            if (window.getComputedStyle(BasicConfig).display === 'block' || window.getComputedStyle(AboutPlugins).display === 'block' || window.getComputedStyle(UpdatePlugins).display === 'block' ) {
                button.style.display = 'none';
            }else{
                button.style.display = 'block';
            }
            // 表单元素添加滚动条
            form.setAttribute("style",'overflow:auto');
            for (const elementId of showElements) {
                if (elementId!== undefined) {
                    const element = document.getElementById(elementId);
                    if (element) {
                        element.style.display = "block";
                    }
                }
            }
            // 获取按钮，添加样式
            var ButtonStyle = document.getElementById(buttonId);

            var buttons = document.getElementsByTagName('button');
            for (var i = 0; i < buttons.length; i++) {
                if (buttons[i].id && buttons[i].id !== buttonId) {
                    // 按钮移除活动状态
                    buttons[i].classList.remove("active");
                }
            } 
            ButtonStyle.classList.add("active");
        }
    </script>
